import logo from './udara.jpg';
import './App.css';

let status = 0; // liked or not

function LikeButton(){
  return (
    <button onClick={handleLike} className='like-btn'>
      <i class="bi bi-star"></i>
    </button> // ret btn w onclick
  );
}

const handleLike = () => { // like button fun
  alert("U liked!");
  LikeButton
};


function App() {
  return (
    <div className='container'>
      <span className='profile-data'>
        <h2>Udara Neminda</h2>
        <img src={logo} alt="aa" className='dp'/>
      </span>
      <LikeButton liked={status}/>
    </div>
  );
}

export default App;
