//Imports//
import logo from './udara.jpg';
import './App.css';

//Global Vars//

let status = Unliked; // liked or not

//Functions//

function LikeButton({onLike}){
  return (
    <button className='like-btn' onClick={handleLikeConstFun}>

      <i class="bi bi-star"></i>
      {} {onLike}

    </button> // ret btn w onclick
  );
}

//ConstFuns//

const handleLikeConstFun = (status) => { // like button fun
  //alert("U liked!");
  if(status == Unliked){
    status = Liked;
  }else{
    status = Unliked;
  }
};

//App//

function App() {

  //Return//

  return (

    <div className='container'>

      <span className='profile-data'>

        <h2>Udara Neminda</h2>

        <img src={logo} alt="aa" className='dp'/>

      </span>

      <LikeButton onLike={status}/>

    </div>
  );
}
//Export//
export default App;
